// Mobile menu wrapper component with industry-specific header
function NavWrapper({ children }) {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(typeof window !== 'undefined' && window.innerWidth < 768);
  const location = useLocation();

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    setMenuOpen(false);
  }, [location]);

  return (
    <>
      <nav style={styles.nav}>
        <Link to="/" style={styles.navBrand}>
          <span style={styles.brandText}>Prosper Pie Co</span>
        </Link>

        {isMobile ? (
          <div style={styles.mobileActions}>
            <button
              onClick={() => setMenuOpen(!menuOpen)}
              style={styles.hamburger}
              aria-label="Toggle menu"
            >
              {menuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>
        ) : (
          <>
            
            <div style={styles.navLinks}>
            <Link to="/" style={styles.navLink}>Home</Link>
            <Link to="/about" style={styles.navLink}>About</Link>
            <Link to="/contact" style={styles.navLink}>Contact</Link>
            <Link to="/menu" style={styles.navLink}>Menu</Link>
            <Link to="/gallery" style={styles.navLink}>Gallery</Link>
            <Link to="/testimonials" style={styles.navLink}>Testimonials</Link>
            <Link to="/faq" style={styles.navLink}>Faq</Link>
            </div>
            <div style={styles.navActions}>
              
            <span style={styles.hoursDisplay}>
              <Clock size={14} />
              Open today: 11am - 10pm
            </span>
              
              
              
              
            <Link to="/order" style={styles.primaryCta}>
              <ShoppingBag size={16} />
              Order Online
            </Link>
              
            <Link to="/reservations" style={styles.secondaryCta}>
              <Calendar size={16} />
              Reservations
            </Link>

            </div>
          </>
        )}
      </nav>

      {isMobile && menuOpen && (
        <div style={styles.mobileMenuOverlay} onClick={() => setMenuOpen(false)}>
          <div style={styles.mobileMenu} onClick={(e) => e.stopPropagation()}>
            <Link to="/" style={styles.mobileNavLink}>Home</Link>
            <Link to="/about" style={styles.mobileNavLink}>About</Link>
            <Link to="/contact" style={styles.mobileNavLink}>Contact</Link>
            <Link to="/menu" style={styles.mobileNavLink}>Menu</Link>
            <Link to="/gallery" style={styles.mobileNavLink}>Gallery</Link>
            <Link to="/testimonials" style={styles.mobileNavLink}>Testimonials</Link>
            <Link to="/faq" style={styles.mobileNavLink}>Faq</Link>
            <div style={styles.mobileCtas}>
              <Link to="/order" style={styles.mobilePrimaryCta}>Order Online</Link>
              <Link to="/reservations" style={styles.mobileSecondaryCta}>Reservations</Link>
            </div>
            <div style={styles.mobileAuthButtons}>

            </div>
          </div>
        </div>
      )}
      {children}
    </>
  );
}

function App() {
  return (
    
    <BrowserRouter>
      <div style={styles.app}>
        <NavWrapper>
          {/* Main Content */}
          <main style={styles.main}>
            <Routes>
              <Route path="/" element={<HomePage />} />
              <Route path="/about" element={<AboutPage />} />
              <Route path="/contact" element={<ContactPage />} />
              <Route path="/menu" element={<MenuPage />} />
              <Route path="/gallery" element={<GalleryPage />} />
              <Route path="/testimonials" element={<TestimonialsPage />} />
              <Route path="/faq" element={<FaqPage />} />
            </Routes>
          </main>
          {/* Footer */}
          <footer style={styles.footer}>
            <p>Â© 2026 Prosper-Pie-Co-. All rights reserved.</p>
          </footer>
        </NavWrapper>
      </div>
    </BrowserRouter>
    
  );
}
